create table cast (id bigint generated by default as identity, cast_order integer not null, character varchar(255), name varchar(255), credit_id bigint, cast_id bigint, primary key (id));
create table credit (id bigint generated by default as identity, primary key (id));
create table crew (id bigint generated by default as identity, department varchar(255), job varchar(255), name varchar(255), credit_id bigint, crew_id bigint, primary key (id));
create table genre (id bigint generated by default as identity, name varchar(255), tmdb_id bigint, movie_id bigint, genre_id bigint, primary key (id));
create table movie (id bigint generated by default as identity, backdrop_path varchar(255), budget integer not null, homepage varchar(255), is_adult boolean not null, original_language varchar(255), original_title varchar(255), overview clob, popularity float, poster_path varchar(255), release_date varchar(255), revenue integer not null, status varchar(255), title varchar(255), tmdb_id bigint, vote_average float, vote_count integer not null, credit_id bigint not null, primary key (id));
create table person (id bigint generated by default as identity, biography clob, birthday varchar(255), deathday varchar(255), gender integer not null, imdb_id varchar(255), known_for_department varchar(255), name varchar(255), place_of_birth varchar(255), popularity float, profile_path varchar(255), tmdb_id bigint, primary key (id));
create table production_company (id bigint generated by default as identity, logo_path varchar(255), name varchar(255), origin_country varchar(255), primary key (id));
create table production_country (id bigint generated by default as identity, iso_3166_1 varchar(255), name varchar(255), movie_id bigint, primary key (id));
